import {React ,useState,useEffect} from 'react';
import './Manreq.css';
import axios from "axios";


function BmanageReq() {

    const [Suserdata, setSUserData] = useState([]);
    const [Sbidata,setSBidata]= useState([]);
    const buyer=JSON.parse(localStorage.getItem("buyer"));
    const buyerId=buyer.map(x=>x._id);
    const bidreq=JSON.parse(localStorage.getItem("bidrequest"));
    

    useEffect(() => {
        getProject();
        getBidRequest();
      }, []);
    
      const getProject = async () => {
       
     
        await axios.get("http://localhost:9999/retrieveProject/"+buyerId[0]).then((response)=>{
         
           
            setSUserData(response.data);
        });
        
       
      };

       const getBidRequest = async () => {
       
     
        await axios.get("http://localhost:9999/retrieveOrderRequest/" +bidreq._id).then((response)=>{
         
           
            setSBidata(response.data);
            console.log((response.data))
        });
        
       
      };
  return (
    <div>
    <div className='p-4'>
        <h1>My Projects</h1>
        <div>
        <table className='tabreq'>
                <thead>
                    <tr>
                        <th>Buyer Id</th>
                        <th>Project Id</th>
                        <th>title</th>
                        <th>description</th>
                        <th>Category</th>
                        <th>Duration</th>
                        <th>Price</th>
                        <th>Action</th>

                        
                        
                    </tr>
                </thead>
                <tbody>
                {Suserdata.map(data=>(
                       <tr>
                        <td >{data.buyerId}</td>
                        <td >{data._id}</td>
                        <td>{data.title}</td>
                        <td>{data.description}</td>
                        <td>{data.category}</td>
                        <td>{data.duration}</td>
                        <td>Rs.{data.price}</td>
                        <td><button>Accept</button></td>
                        
                      
                       </tr>


    ))}
   
                </tbody>
            </table>
        </div>
        <div>
        <h1>Bid Requests</h1>
        <table className='tabreq'>
                <thead>
                    <tr>
                        <th>Buyer Id</th>
                        <th>Seller Id</th>
                        <th>title</th>
                        <th>description</th>
                        <th>Category</th>
                        <th>Duration</th>
                        <th>Budget</th>
                        
                        
                    </tr>
                </thead>
                <tbody>
                
                       <tr>
                        <td>{bidreq.buyerId}</td>
                        <td>{bidreq.sellerId}</td>
                        <td>{bidreq.title}</td>
                        <td>{bidreq.description}</td>
                        <td>{bidreq.category}</td>
                        <td>{bidreq.duration}</td>
                        <td>{bidreq.price}</td>
                        
                        
                      
                       </tr>


   
   
                </tbody>
            </table>
        </div>
        </div>
    </div>
  )
}

export default BmanageReq;
